options.state:
{% set dc = states('sensor.ac500_dc_input_power') | float(0) %}
{% set ac_in = states('sensor.ac500_ac_input_power') | float(0) %}
{% set ac_out = states('sensor.ac500_ac_output_power') | float(0) %}
{% set discharging_raw = dc + ac_in - ac_out %}
{% set efficiency = -1.0 %}
{% set discharging = discharging_raw * efficiency %}
{{ discharging if discharging_raw < 0 else 0 }}
