options.state:
{% set now = now() %}
{% set weekday = now.weekday() %}
{% set peak_start = now.replace(hour=0, minute=0, second=0, microsecond=0) %}
{% set peak_end = now.replace(hour=21, minute=0, second=0, microsecond=0) %}

{% if weekday < 5 and now >= peak_start and now < peak_end %}
  {{ ((peak_end - now).total_seconds() / 3600) | round(2) }}
{% else %}
  0
{% endif %}
